theme.customCss:
.typebot-container {
  background: #000;
}

.typebot-chat-view > .flex:first-child {
    padding-top: 30px;
}
[elementtiming="Bubble image"]{
  max-width:250px;
}
.typebot-button {
  background-color: #C10000!important;
  color:#fff!important; 
  border: none;
  border-bottom:4px solid #E53B3B;
  /*filter: drop-shadow(0 5px 8px #0041D8);*/
  animation: pulse 2s infinite;
  cursor:pointer !important;
}

@keyframes pulse {
0% {
  transform: scale(0.97);
  box-shadow: 0 0 0 0 rgba(253, 253, 253, 0.7);
}

70% {
  transform: scale(1.03);
  box-shadow: 0 0 0 10px rgba(0, 0, 0, 0);
}
100% {
  transform: scale(0.97);
  box-shadow: 0 0 0 0 rgba(0, 0, 0, 0);
}
}

.bubble1, .bubble2, .bubble3 {
    background-color: #fff !important;
    opacity: .5;
}
.typebot-host-bubble{
    filter: drop-shadow(0 1.5px 0.5px rgba(11,20,26,.13));
    width:fit-content;
    max-width: 90%;
    border-radius:0 8px 8px 8px;
    color:#fff !important;
}
.typebot-host-bubble>.bubble-typing {
  border-radius: 0 8px 8px 8px;
  background: #202C33 !important; /* bg do bot */
}
.typebot-host-bubble>.bubble-typing:after {
    position: absolute;
    content: "";
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0px 10px 10px 0;
    border-color: transparent #202C33 transparent transparent;
    top: 0;
    left: -10px;
}

.typebot-guest-bubble {
  color:#fff!important;
  background: #DF2C2C!important; /* bg do user */
  position: relative;
  border-radius: 5px 0 5px 5px;
  max-width: 90%;
  filter: drop-shadow(0 1.5px 0.5px rgba(11,20,26,.13));
}
.typebot-guest-bubble .feno-bubble {
color: rgb(255, 255, 255)!important;
}

.typebot-guest-bubble:after {
  position: absolute;
  content: "";
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 0px 0 10px 10px;
  border-color: transparent transparent transparent #DF2C2C;
  top: 0;
  right: -10px;
  border-style: solid;
}
/* ######### */
.typebot-input{
  background-color:#2A3942!important;
  color:#fff !important;
  border-radius: 50px !important;
  box-shadow: 0 2px 6px -1px rgba(0,0,0,.1);
  bottom: 0;
  left:0;
  height:78px;
  z-index: 1;
  border: solid 12px #202C33;
  transition:.5s easy;
  position:fixed;
  min-width:100%;
  box-shadow: 0px 120px 0px 120px #202C33;
}

.typebot-input input::placeholder{
  color: #8696A0 !important;
}

.typebot-input .typebot-button {
  content: "";
  border-radius: 50%; 
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 50px; 
  height: 50px; 
  border: none;
  font-size: 0px;
  color: transparent;
  margin-bottom:2px;
}

.typebot-input .typebot-button::before {
  content: "";
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 25px;
  color:#fff;
  height: 25px;
  background-repeat: no-repeat;
  background-size: cover;
