// Desativar DevTools
window.addEventListener('keydown', function(event) {
    if (event.key === 'F12' || (event.ctrlKey && event.shiftKey && event.key === 'C') || (event.ctrlKey && event.key === 'u')) {
        event.preventDefault();
        event.stopPropagation();
    }
});

// Desativar o menu de contexto (bot√£o direito do mouse)
window.addEventListener('contextmenu', function(event) {
    event.preventDefault();
});

theme.customCss:
.typebot-container {
  background: #212425;
}

.typebot-chat-view {
  padding-top: 5.5rem;;
  padding-bottom: 1rem;
}

.typebot-input {
  position: fixed;
  bottom: 2rem;
  margin-inline: auto;
  left: 0;
  right: 0;  
  max-width: 800px !important;  
}

.typebot-input.custom-input {
  border-radius: 100vw;
  padding: 0;
  opacity: 1;
}

.typebot-input.custom-input input {
  padding: 0 0 0 1rem !important;
}

.typebot-input > div {
  width: 100%;
}

.user-metadata {
  display: flex;
  justify-content: center;
  align-items: center;
  justify-content: center;
  align-items: center;
  text-align: center;
  color: white;
  margin-block: 1.5rem;
}

.user-metadata a {
  text-decoration: none;
  align-items: center;
  text-align: center;
  padding: .25rem .75rem;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 10px;
  color: #ddd;
  border-radius: 2px;
}

/* Estilos para centralizar a imagem */
.user-metadata img {
  max-width: 100%; /* Evita que a imagem se estenda para al√©m do cont√™iner */
  height: auto; /* Mant√©m a propor√ß√£o original da imagem */
}

.primary-info .name {
    font-weight: bold;
}

.secondary-info {
    opacity: .7;
}


.message-input {
    display: flex;
    padding: 6px 8px;
    border-radius: 100vw;
    border: 2px dashed #707070;
    justify-content: center;
    max-width: 100%;
    width: 100%;
    margin-inline: auto;
    /* position: fixed; */
    bottom: 1rem;
    left: 0;
    right: 0;
    z-index: 9999;
    background: black;
    align-items: center;
}

.message-input .cam {
    border-radius: 50%;
    background-color: #0099fd;
    display: grid;
    place-content: center;
    aspect-ratio: 1;
    width: 50px;
}



.message-input > * {
    display: flex;
}

.message-input .input, 
.message-input input {
    width: 100%;
    border: 0;
    padding-left: .35rem;
    color: white;
    font-weight: bold;
    font-size: 17px;
    background: transparent;
}

.message-input .icons > * {
    width: 40px;
    display: grid;
    place-content: center;

}
